import{q as i,b as r,s as m,t as l,g as a,x as k,y as n,z as s}from"./index.e91f46c8.js";const p={class:"markdown-body"},g={class:"story-h1"},h=a("p",null,"Kas on veel kuskil see kadakaaru, muru t\xE4is madalaid lillasid ohakatutte, mis n\xE4isid nii enneolematult ilusad? Soojas vihmasajus olid nad s\xE4delevad ja pehmed, nende okkalised lehedki olid pehmed. Panin neile oma palja jalatalla, et ka tema neid n\xE4eks.",-1),c=a("p",null,"Ja m\xF5tlesin: keegi teine ei tea seda, pole seda iialgi tundnud! Panin oma n\xE4o vastu vihma s\xF5rmeotsi. Ja tajusin nende teravaid ning \xFChtlasi hajuvaid puudutusi.",-1),_=a("p",null,"See oli nagu purjusolek. Endasse mittemahtumine. Ses oli jagamatuse piina. \xDCksinduseh\xE4da. Marraskil ning narmendavat uhkust.",-1),v=a("p",null,"Olin laps ja mitte enam laps. Elasin iseendaga ja loodusega. Ma pelgasin inimesi. Pelgasin ja igatsesin l\xE4bik\xE4imist nendega, mingit \xFCrgset suhet nagu muu loodusega.",-1),j=a("p",null,"Aga v\xF5ib-olla pole t\xF5esti keegi teine peale minu kunagi tundnud seda muru, neid suuri lillasid \xF5ienupp-ohakaid, enneolematult ilusaid, seda vihma?",-1),b=a("p",null,"Ja ma v\xF5iksin selle inimestele anda, et mul oleks mingi l\xE4bik\xE4imine nendega, armastus?",-1),V=a("hr",{class:"mb-6"},null,-1),y=a("p",null,"Leidsin paberite hulgast selle kirjapaneku ja j\xE4in ta kohal m\xF5ttesse: kas ei kajastu ses loomeimpulss? Looming kui suhtlemisvorm? V\xE4ljap\xE4\xE4s \xFCksindusest? V\xF5ib-olla.",-1),M=a("p",null,"Murdeiga on avastuste aeg, suurim ellu s\xF6\xF6st ja eluaplus. Ja alatine enesevaatlus ning k\xFCsimus: kes sa oled? Ei saada ega saada selgusele, sest kogu aeg sinus midagi muutub, puruneb, s\xFCnnib. Nahk on aga \xFClihell, k\xF5ik riivab, teeb haiget. \xDCmbritsev maailm asub suurde vastasseisu, ta on ehmatavalt ebat\xE4iuslik ja r\xFCndamishimuline.",-1),f=a("p",null,"P\xFCha maailmavalu, unustatud, mahasalatud, v\xE4lja naerdud ja siiski p\xFCha maailmavalu!",-1),w=a("p",null,"J\xF5utakse \xE4ratundmisele enesest kui indiviidist, ainulaadsest, teistest erinevast olendist. Suhted kaasinimestega kaotavad oma igap\xE4evase endastm\xF5istetavuse: kes nad on, need teised? Nad peletavad eemale ja t\xF5mbavad ligi.",-1),x=a("p",null,"Veel pole m\xF5istetav osadus ja lepitus, ja ligimene on muidu \xFCks vanaaegne s\xF5na, millel p\xFChap\xE4evakooli imal maik.",-1),D=a("ul",null,[a("li",null,"On sul oma koht, kus k\xE4id m\xF5tlemas elu ja maailma \xFCle? Kus ja milline, kui oled n\xF5us teistega jagama?"),a("li",null,"Milline on looduse roll sinu elus?"),a("li",null,"Kust sa ammutaid ideid, energiat?"),a("li",null,"Oled sa tundnud maailmavalu? Mis puhul?"),a("li",null,"Mida t\xE4hendavad sinu jaoks s\xF5nad \u201Cosadus\u201D ja \u201Clepitus\u201D?")],-1),S=a("ul",null,[a("li",null,[s("Tekst: "),a("strong",null,"Debora Vaarandi"),s(" \u201CKaudu mulla ja asfaldi\u201D, "),a("strong",null,"Endel Priideli"),s(" kogumikus \u201CTeose s\xFCnd\u201D, Eesti Raamat, 1976.")]),a("li",null,[s("Foto: "),a("strong",null,"Merit Karise")])],-1),P="debora-vaarandi-kadakaaru",B="Debora Vaarandi",O=null,T=[{src:"debora-vaarandi-kadakaaru.webp"}],A=[["ei","ole"]],C=["Debora Vaarandi"],L={__name:"debora-vaarandi-kadakaaru",setup(E,{expose:u}){const e={slug:"debora-vaarandi-kadakaaru",author:"Debora Vaarandi",origin:null,images:[{src:"debora-vaarandi-kadakaaru.webp"}],dictionary:[["ei","ole"]],tags:["Debora Vaarandi"]};return u({frontmatter:e}),(J,K)=>{const o=i("story-author"),t=i("details-wrapper"),d=i("MarkdownWrapper");return r(),m(d,{frontmatter:e},{default:l(()=>[a("div",p,[a("h1",g,k(e.title),1),h,c,_,v,j,b,V,y,M,f,w,x,n(o,{author:e.author,origin:e.origin},null,8,["author","origin"]),n(t,{summary:"Mis m\xF5tted tekkisid?"},{default:l(()=>[D]),_:1}),n(t,{summary:"Allikas",class:"text-sm",icon:"IconSources"},{default:l(()=>[S]),_:1})])]),_:1})}}};export{B as author,L as default,A as dictionary,T as images,O as origin,P as slug,C as tags};
