import{q as u,b as m,s as r,t as s,g as a,x as k,y as l,z as i}from"./index.e91f46c8.js";const p={class:"markdown-body"},h={class:"story-h1"},g=a("p",null,"Preedik on vana mees ja hommikutunnil l\xE4heb ta Abruka sadama poole m\xF6\xF6da teed, mis on iidne. Puud, p\xF5\xF5sad, majad ja aiad, h\xE4\xE4ltest ja l\xF5hnadest r\xE4\xE4kimata, on nooremad kui see kivir\xE4hka tallatud rannatee. Vaid meri eespool ja taevas pea kohal on iidsemad. Meri n\xE4itab end kahvatusiniselt, taevas on sama v\xE4rvi ja pilvitu. Septembri keskpaiga kohta on hommik karge ja jahe. Isegi nii jahe, et meri hakkab ootamatult t\xF5stma. T\xF5stab kauged Kuramaa rannad ja isegi Ruhnu metsaladvad p\xE4ris silme ette. Targemad inimesed kutsuvad niisugust n\xE4htust miraa\u017Eiks.",-1),j=a("p",null,"Seda juhtub s\xFCgise poole ikka, et meri t\xF5stab, palju kaugeid paiku on Preedik niiviisi Abruka rannalt \xE4ra n\xE4inud. Aga see k\xF5ik on pisike ime, v\xF5rreldes sellega, kui kaugeid aegu ja asju v\xF5ib su oma m\xE4lu ootamatult ja ootamatu selgusega silme ette tuua.",-1),v=a("p",null,"Preedik l\xE4heb sadamasse mitmendat p\xE4eva j\xE4rjest. K\xF5ik need hommikud on olnud t\xFChjad. R\xE4ime pole. \u201CMis sa sinna ometi otsid,\u201D \xFCtles naine, kui ta kodust v\xE4lja astus. \u201CMeitel pole toidust puudus.\u201D Naise aru. Kuidas pole toidust puudus, kui talvine r\xE4im on sisse soolamata.",-1),_=a("p",null,"Ei maksa m\xF5telda, et r\xE4im on ainult leiva- v\xF5i kartulik\xF5rvane. R\xE4imep\xFC\xFCk on olnud elu. Elu on olnud r\xE4imep\xFC\xFCk. Ja mis elu \xFCldse oleks olnud, kui poleks olnud r\xE4imep\xFC\xFCki?",-1),c=a("p",null,"Isegi ajaarvamine k\xE4ib siin r\xE4ime j\xE4rgi.",-1),b=a("p",null,"\xDChel aastal oli palju kevadist m\xF5rrar\xE4ime, s\xFCgisene v\xF5rgup\xFC\xFCk j\xE4i aga kasinaks. J\xE4rgmisel aastal oli vastupidi - kevadel olid kastm\xF5rrad nii kaua t\xFChjad, et kajakad kaotasid kannatuse ja l\xF5ugasid n\xE4rviliselt mere kohal. S\xFCgis andis aga v\xF5rgur\xE4ime n\xF5nda, et ei j\xF5udnud maale vedada.",-1),w=a("p",null,"Lugenud inimene \xFCtleb, et 1956. aastal toimusid Ungari s\xFCndmused ja Melbourne\u2019i ol\xFCmpiam\xE4ngud. Preedik \xFCtleb, et elades ei p\xFC\xFCta enam \xFChe suvega nii palju kala merest v\xE4lja kui 1956. aastal. Tark teab, et Gagarin lendas kosmosesse 1961. aasta aprillikuus. Preedik teab, et 1961. aasta kevad oli esimene, kui Abruka mehed enam vitsm\xF5rdu meresse ei pannud. R\xE4ime p\xFC\xFCti ainult suurte kastm\xF5rdadega.",-1),S=a("p",null,"Jaagupi Juuli ei surnud 1957. aastal. Juuli suri sel aastal, kui P\xE4rna Albert sai Pika nina m\xF5rrast \xFChe hommikuga kaksk\xFCmmend tonni kala. Velskri Kristjan ei s\xFCndinud 1963. aasta maikuus. Kristjan s\xFCndis siis, kui tuul t\xF5i mai l\xF5pus j\xE4\xE4 tagasi ning see muljus mitu p\xFC\xFCnist \xE4ra.",-1),K=a("p",null,"Preedik ei tea t\xE4pselt sedagi, et ta v\xF5ttis naise 1948. aastal. Aga seda m\xE4letab ta t\xE4pselt, et 1948. aasta augustis p\xE4\xE4ses karjamaalt vabeaeda kaks k\xFCla lehma, kes risusid sarvedega mitu uut r\xE4imev\xF5rku ribadeks.",-1),T=a("p",null,"T\xE4nastki p\xE4eva ei seo Preedik aastate p\xE4rast Rooma paavsti visiidiga Tallinna. 10. september j\xE4\xE4b meelde sellega, et l\xF5puks ometi t\xF5id mehed Veiserahust kolm tonni rasvast v\xF5rgur\xE4ime. V\xF5ib muidugi juhtuda, et naised hakkavad hiljem r\xE4\xE4kima, et t\xE4nane kalasaak oli paavsti kingitus Abruka rahvale. Lugu oleks ju iseenesest ilus ja teeks paavstigi pisut r\xF5\xF5msamaks. On teine kogu aeg mureliku n\xE4oga ja vaatab muudkui enese ette maha, otsekui oleks meel murtud v\xF5imetusest inimesi aidata ja lohutada.",-1),f=a("p",null,"Kolm tonni r\xE4ime leidsid Abruka mehed Veiserahust ise \xFCles. Aega v\xF5ttis mitu n\xE4dalat, kui aus olla, aga \xFCles leidsid. Muidugi, Abruka meeste seas pole enam selliseid selgeltn\xE4gijaid nagu kadunud Tamme Sass.",-1),P=a("p",null,"J\xE4\xE4b m\xF5istatuseks, kuidas k\xFCll tema k\xF5igist paremini suurest merest kala \xFCles leidis. Uhke ta selle \xFCle polnud, imelikul kombel vahtis temagi tavaliselt enda ette maha nagu n\xFC\xFCd paavst. V\xF5ib-olla oli tal terasem n\xE4gemine ja terasem haistmine - midagi pidi temas olema teistmoodi kui tavalises kalamehes. V\xF5ib-olla ta polnudki meiesugune, v\xF5ib-olla oli kalakull. Preedik p\xFC\xFCdis temaga \xFChes paadis kolmk\xFCmmend aastat, ja m\xF5nikord ajas otse hirmu nahka, kuidas Sass veel n\xE4htamatu kalaparve asukoha eksimatult \xE4ra haistis.",-1),y=a("p",null,"\u201CKuidas sa teadsid?\u201D k\xFCsiti Sassilt.",-1),A=a("p",null,"\u201COli selline tunne,\u201D vaatas Sass omaette s\xFC\xFCdlaslikult maha.",-1),J=a("p",null,"Raamatuid on palju kirjutatud, aga r\xE4imep\xFC\xFCgi \xF5pikut pole Preedik oma silmaga n\xE4inud. Sellep\xE4rast pole niisugust \xF5pikut olemas, et r\xE4imep\xFC\xFCk on liiga keeruline v\xE4rk, liiga peen kunst, et raamatu sisse \xE4ra mahuks. Sassi sisse mahtus \u2013 k\xF5ik mere - tujud, tusad ja tuuled, k\xF5ik pinna- ja p\xF5hjahoovused, k\xF5ik k\xF5rg- ja madalr\xF5hkkonnad, t\xE4is- ja noorkuu, t\xE4histaevas ja ramptihe udulaam, lahevee kiire soojenemine ja j\xE4rsk jahenemine. Kui Eestis oleks olnud kalap\xFC\xFCgi akadeemia, oleks Tamme Sass ilmtingimata olnud r\xE4imep\xFC\xFCgi akadeemik.",-1),M=a("ul",null,[a("li",null,"Kuidas on sinu suhe r\xE4imega? S\xF6\xF6d? Oskad valmistada? P\xFC\xFCad?"),a("li",null,"J\xFCri Tuulik on \xF6elnud nii: \u201EK\xF5ige suurem vajadus muhedate inimeste j\xE4rele on just v\xE4ikestes kollektiivides. V\xE4ikesel saarel v\xF5i v\xF5rgupaadis on v\xE4ga t\xE4htis, et oleks hulgas muhedaid inimesi.\u201C Oled sa n\xF5us? Miks on see nii?"),a("li",null,"Mis juhtus Saaremaal, Eestis ja maailmas siis, kui sina s\xFCndisid? Mis oleks sulle oluline teema, millega oma elu s\xFCndmusi v\xF5rdleksid nagu \xFClal loos r\xE4imep\xFC\xFCgiga? Ja vastupidi, mida tegid sina, kui Saaremaal, Eestis, maailmas toimusid olulised s\xFCndmused?")],-1),V=a("h3",null,"Kadakar\xE4imed",-1),R=a("ul",null,[a("li",null,"20 r\xE4imefileed"),a("li",null,"1 tl suhkrut"),a("li",null,"1 sl kadakamarju"),a("li",null,"3 sl tilli"),a("li",null,"1 dl veini\xE4\xE4dikat")],-1),x=a("p",null,"Fileed panna taldrikule. Suhkur, purustatud kadakamarjad ja till riputada peale. Seej\xE4rel kallata peale \xE4\xE4dikas. Lasta \xF6\xF6p\xE4ev t\xF5mmata. Serveerida k\xFClmalt.",-1),E=a("h3",null,"Kalaleem",-1),I=a("ul",null,[a("li",null,"750 g r\xE4ime"),a("li",null,"1 l vett"),a("li",null,"2 tl soola"),a("li",null,"2 porgandit"),a("li",null,"sellerit"),a("li",null,"pastinaaki"),a("li",null,"5 tera v\xFCrtsi"),a("li",null,"5 nelki"),a("li",null,"2 loorberilehte")],-1),L=a("p",null,"R\xE4imed rappida ja panna (ka pead ja mari) soolaga maitsestatud k\xFClma veega keema. Riisuda vaht, lisada maitsejuured ja -ained. Leemel lasta keeda tasasel tulel umbes tund, siis kurnata.",-1),N=a("p",null,"Pakkuda eelroana koos r\xF6stleiva kuubikute v\xF5i keedetud munaga. V\xF5ib kasutada ka mitmesuguste kalasuppide valmistamiseks. T\xFC\xFCbilt traditsiooniline mahe p\xF5hjamaine kalaleem, milles kala maitse tuleb selgesti esile.",-1),O=a("h3",null,"Ahjus k\xFCpsetatud r\xE4imed",-1),B=a("ul",null,[a("li",null,"500 g r\xE4ime"),a("li",null,"3 sl rukkijahu"),a("li",null,"1/2 sl soola"),a("li",null,"1 sl rasvainet")],-1),F=a("p",null,"Puhastatud r\xE4imi veeretada soolaga segatud jahus, laduda kalad v\xF5itatud vormi, \xFClej\xE4\xE4nud rasvaine panna t\xFCkkidena peale. K\xFCpsetada ahjus.",-1),C=a("h3",null,"Kapteniproua r\xE4imevorm",-1),U=a("ul",null,[a("li",null,"900 g r\xE4imefileed"),a("li",null,"2 sibulat"),a("li",null,"2 k\xFC\xFCslauguk\xFC\xFCnt"),a("li",null,"2 dl koort"),a("li",null,"2 dl piima"),a("li",null,"1 sl v\xF5id"),a("li",null,"2 tl soola"),a("li",null,"valget pipart, rohkesti tilli")],-1),W=a("p",null,"M\xE4\xE4ritud ahjuvormi laduda kihiti r\xE4imefileed, ribastatud sibul, peenestatud k\xFC\xFCslauk ja maitseained. Koor segada piimaga ja kallata r\xE4imedele. K\xFCpsetada 225\xB0 ahjus pool tundi. Serveerida ahjukartulite ja karaskiga.",-1),q=a("ul",null,[a("li",null,[i("Tekstil\xF5igud ja retseptid: "),a("strong",null,"J\xFCri Tuulik"),i(", \u201CR\xE4im, pisike kena kala: valitud lugusid ja toiduretsepte\u201D, kirjastus Kadmirell, 2008.")]),a("li",null,[i("J\xFCri Tuuiku elu ja loomingu kohta saab lugeda siit: "),a("a",{href:"http://www.looming.ee/artiklid/juri-tuulik-22-ii-1940-3-vii-2014/"},"http://www.looming.ee/artiklid/juri-tuulik-22-ii-1940-3-vii-2014/")]),a("li",null,[i("R\xE4ime nimetused: "),a("a",{href:"https://www.kalapeedia.ee/5808.html"},"https://www.kalapeedia.ee/5808.html")]),a("li",null,[i("Foto: "),a("strong",null,"Eilika Trummar")])],-1),Q="aa",X="J\xFCri Tuulik",Y=null,Z=[{src:"jyri-tuulik-raim.webp"}],$=[["ei","ole"]],aa=[],ea={__name:"jyri-tuulik-raim",setup(z,{expose:n}){const e={slug:"aa",author:"J\xFCri Tuulik",origin:null,images:[{src:"jyri-tuulik-raim.webp"}],dictionary:[["ei","ole"]],tags:[]};return n({frontmatter:e}),(D,G)=>{const d=u("story-author"),t=u("details-wrapper"),o=u("MarkdownWrapper");return m(),r(o,{frontmatter:e},{default:s(()=>[a("div",p,[a("h1",h,k(e.title),1),g,j,v,_,c,b,w,S,K,T,f,P,y,A,J,l(d,{author:e.author,origin:e.origin},null,8,["author","origin"]),l(t,{summary:"Mis m\xF5tted tekkisid?"},{default:s(()=>[M]),_:1}),l(t,{summary:"Tegemiseks",icon:"IconKnifeFork"},{default:s(()=>[V,R,x,E,I,L,N,O,B,F,C,U,W]),_:1}),l(t,{summary:"Allikas",class:"text-sm",icon:"IconSources"},{default:s(()=>[q]),_:1})])]),_:1})}}};export{X as author,ea as default,$ as dictionary,Z as images,Y as origin,Q as slug,aa as tags};
