import{h as s,o as u,b as k,k as t,a,t as m,i as l,m as e}from"./vendor.518a3394.js";const p={class:"markdown-body"},h={class:"story-h1"},v=a("p",null,"Mari Lepik S\xF5rvest kirjutab mardip\xE4evast: \u201CMardilaup\xE4evaks katkeb noorte meeste kannatus. Esiti \xFChe-kahekaupa kokkusaamistest ning elubeluugi vahelt poolkogemata leitud pudrukausside t\xFChjendamisest kasvab mardilaup\xE4evaks isu juba enamaks.",-1),g=a("p",null,"Nii otsivadki k\xFCla noored mehed ning hakkajamad neiud omale miskit sandemat selga, panevad valss-palged ette v\xF5i tahmavad silmn\xE4o sootuks ning hakkavad peresid pidi k\xE4ima. Kui vaid kohe \xE4ra ei tuntaks, saab l\xF5busam!",-1),b=a("p",null,"K\xF5ige mehekam hakkab mardi-isaks, teised trallivad tema j\xE4rgi. Muidugi kotipoiss kah \u2013 tema peab tugev olema! Ja kena kott peab tal olema.",-1),c=a("p",null,[e("Kui kuskil peres lapsi on, saab nalja. Nagamannid ei karda, laulavad vemmalv\xE4rssi vastu. Pole viga \u2013 k\xFCll matak neid taltsutab. Pisemad kaovad martide kolistamist ja laulu kuuldes juba targu aseme alla. Naisperega jutulesaamiseks k\xFCsitakse t\xF6\xF6j\xE4rge n\xE4ha \u2013 kuidas selle ketramisega siin peres siis l\xE4inud on ka? Kas annab mardi jalad v\xE4rskete l\xF5ngavihtidega kinni matta?"),a("br"),e(" Nalja peab saama!")],-1),j=a("p",null,"Mis perenaine kostab, kui kari poriseid kaltsakaid toas pilli j\xF6ristab ning tantsu vihub? Kas peremees t\xE4ib t\xFCki liha anda v\xF5i kallatakse kotti vaid vaanat\xE4is palte? Kas peret\xFCtar kilkab, kui teda v\xE4heke vitsutada, kui l\xF5ngal on nibu sisse visand? Kas vahvasti marte oma salmiga narrinud poisivolask tuleks ehk aasta p\xE4rast juba kampa v\xF5tta?",-1),_=a("p",null,"Kotipoiss korjab palte, p\xE4hkleid, \xF5unu, leiba ja muud, mis pererahval anda on. Vahepeal oli moes anda vilja \xF5lle tegemiseks\u2026 Siis pidi mardipidu m\xF5ned p\xE4evad ootama.",-1),w=a("p",null,[e("Kui ust lahti ei tehta, siis alles hakkab nalja saama. Puuriitade, vankrite, \xE4mbrite ja kukkedega saab igasugu sigadusi korraldada suuremat kahju s\xFCnnitamata."),a("br"),e(" \u201CMis te tahate?\u201D"),a("br"),e(" \u201CMidagi ei taha, \xF5nne tulime tooma! Kui te selle v\xE4lja teeninud olete! Ja tantsima ning laulma. Omaette ning perega seltsis.\u201D"),a("br"),e(" \u201COlge lahked \u2013 paneme kotti, mis meil anda on ja head teed teil minna!\u201D"),a("br"),e(" Vilja\xF5nn \u2013 sa p\xFCha asi! Et loomad kosuks, et p\xF5llud kasvaks! Et oleks leiba, \xF5lut, lambakintsu ning seak\xFClge!")],-1),K=a("p",null,[e("Dialoog on ajapikku paikakulunud, suur osa sest on ka lauluks saanud. Nii ei peagi iga\xFCks alati osav s\xF5nasepp olema, piisab lauluridade meelespidamisest v\xF5i paremal juhul vaid laulumehe j\xE4rgi kordamisest."),a("br"),e(" Viimases peres \u2013 suurema toa, lahkema pereisa, heldema perenaise ja naeruhimuliste peret\xFCtardega peres \u2013 saab olema pidu. Pilli ja tralliga. Kasukad heidetakse maha, valssn\xE4ud koldesse. Saab s\xFC\xFCa. Saab tantsida. Saab kahekesi \xF5ues t\xE4hti vaatamas k\xE4ia."),a("br"),e(" Mida sa inimese hing veel ihkad!\u201D")],-1),M=a("ul",null,[a("li",null,"Kuidas sul mardip\xE4evaga lood on? Oled marti jooksnud? Kas tead, et mardi- , kadri- ja muud sandid peaksidki olema g\xFCmnaasiumiealised noored, mitte v\xE4ikesed lapsed?"),a("li",null,[e("Mardi- ja kadrip\xE4ev v\xF5i halloween? Eri rahvad t\xE4histavad samasisulisi t\xE4htp\xE4evi eri ajal ja eri moodi. Kes meie pimeda s\xFCgise ikka \xE4gedaks teeb, kui mitte me ise? "),a("a",{href:"https://opleht.ee/2018/10/teeme-rahvakultuuri-ise-endale-agedaks/"},"https://opleht.ee/2018/10/teeme-rahvakultuuri-ise-endale-agedaks/")]),a("li",null,[e("Saaremaa sanditajate kohta saab lugeda siit: "),a("a",{href:"https://xn--srvemaa-90a.ee/souke-sorvemaa/sorve-keel/sandid-ning-onnetoojad-saaremaal/"},"https://s\xF6rvemaa.ee/souke-sorvemaa/sorve-keel/sandid-ning-onnetoojad-saaremaal/")]),a("li",null,[e("Mardi- ja k\xF5igi muude eesti santimisp\xFChade kohta saab lugeda siit: "),a("a",{href:"https://www.folklore.ee/erk/exhibits/show/mardipaev"},"https://www.folklore.ee/erk/exhibits/show/mardipaev")]),a("li",null,[e("Kuula, kuidas S\xF5rve b\xE4nd \u201CK\xFCi\u201D mardilaulu laulab: "),a("a",{href:"https://www.youtube.com/watch?v=-OCvVPVKtl4"},"https://www.youtube.com/watch?v=-OCvVPVKtl4")])],-1),f=a("ul",null,[a("li",null,[a("strong",null,"Mari Lepiku"),e(" lugu: "),a("a",{href:"https://xn--srvemaa-90a.ee/souke-sorvemaa/sorve-keel/sandid-ning-onnetoojad-saaremaal/"},"https://s\xF6rvemaa.ee/souke-sorvemaa/sorve-keel/sandid-ning-onnetoojad-saaremaal/")]),a("li",null,[e("Fotod: "),a("strong",null,"Ingela Auv\xE4\xE4rt"),e(" ja "),a("strong",null,"Sigrid-Merili Kuhi"),e(", Kuressaare ametikooli disainisuuna dekoraator-stilistid")])],-1),N="Mardip\xE4ev",P="mardipaev",O="Mari Lepik",C="S\xF5rvest",E=[{src:"mardisant-1.webp"},{src:"mardisant-2.webp"}],L=[["ei","ole"]],B=["mardip\xE4ev","santimine"],A={setup(S,{expose:n}){const i={title:"Mardip\xE4ev",slug:"mardipaev",author:"Mari Lepik",origin:"S\xF5rvest",images:[{src:"mardisant-1.webp"},{src:"mardisant-2.webp"}],dictionary:[["ei","ole"]],tags:["mardip\xE4ev","santimine"]};return n({frontmatter:i}),(y,V)=>{const r=s("story-author"),o=s("details-wrapper"),d=s("MarkdownWrapper");return u(),k(d,{frontmatter:i},{default:t(()=>[a("div",p,[a("h1",h,m(i.title),1),v,g,b,c,j,_,w,K,l(r,{author:i.author,origin:i.origin},null,8,["author","origin"]),l(o,{summary:"M\xF5tlemiseks ja arutlemiseks"},{default:t(()=>[M]),_:1}),l(o,{summary:"Allikad",class:"text-sm",icon:"IconSources"},{default:t(()=>[f]),_:1})])]),_:1})}}};export{O as author,A as default,L as dictionary,E as images,C as origin,P as slug,B as tags,N as title};
