import{q as t,b as k,s as r,t as s,g as a,x as m,y as i,z as l}from"./index.f42c671b.js";const h={class:"markdown-body"},c={class:"story-h1"},p=a("p",null,"Vanasti eland Vedruka k\xFClas v\xE4ga targad ja usinad inimesed. K\xF6ik armastand t\xF6\xF6d ja nende keskel valitsend \xFCksmeel. Aga olnd ka niisuguseid mehi, kes uhked ja kuulsuse tagaajajad. Need saatnud k\xFCll korda k\xF6iksugu m\xF6tlematuid tegusid. Teised k\xFClad pidand Vedrukast suurt lugu.",-1),g=a("p",null,"Kord akand naaberk\xFClad Vedruka k\xFClast kuulsuse poolest ette minema. Seda kuuldes Vedruka k\xFCla auahned mehed [hakanud] teistele ennen\xE4gematuid tegusid tegema, et Vedruka k\xFCla ikka k\xF6ige ees oleks. Varsti akandki kogu Vedruka k\xFCla elanikud riidlema, nii et \xFCks ei saand enam teisega l\xE4bi. Iga\xFCks akand ahnust taga ajama ja teisi kiusama. Viimaks l\xE4ind koguni nii rumalaks, et \xFCks ei oolind \xFChest, teine teisest. Vedruka oma alva eluviisi p\xE4rast olnd igal pool tuntud.",-1),_=a("p",null,"\xC4kki s\xFCttind k\xFCla p\xF6lema. Kust tuli alguse saand, ei teadnud keegi. K\xFCla p\xF6lend maani maha, ainult m\xF6ned \xFCksikud talud p\xE4\xE4send tuleohust.",-1),v=a("p",null,"Umbes m\xF6ne aasta p\xE4rast olnd uus \xF6itsev k\xFCla vana varemeil. Sellegi k\xE4si ei k\xE4ind paremini kui eelmisel. Juba kolmas k\xFCla vana asemel. Sellegi k\xE4si k\xE4is samuti kui eelmistel.",-1),j=a("p",null,"\u201ESee k\xFCll enam \xF6ige asi ei ole,\u201C m\xF6lgutasid m\xF6ned m\xF6tteid. \u201ETarvis k\xFCla kuhugi mujale ehitada,\u201D nii m\xF6telnud p\xE4\xE4send vedruklased. Juba asutud neljandat k\xFCla ehitama, kuid mitte vana k\xFCla asemele, vaid tast ulk maad eemale.",-1),V=a("p",null,"Uut k\xFCla enam miski \xF6nnetus ei taband, sest inimesed parandand meelt. P\xF6lend k\xFCla asemele on tekkind soo, mida Loona sooks kutsutakse.",-1),y=a("ul",null,[a("li",null,"?")],-1),f=a("ul",null,[a("li",null,[l("Rahvajutt: "),a("a",{href:"http://saaremaa.folklore.ee/wordpress/?p=862"},"http://saaremaa.folklore.ee/wordpress/?p=862")]),a("li",null,[l("Foto: "),a("strong",null,"Merit Karise"),l(),a("em",null,"(foto pole Vedruka k\xFClast, lihtsalt \xFCks vana talumaja)")])],-1),M="Miks k\xFCla \xE4vis?",S="miks-kyla-avis",J="rahvajutt",N="Jutustanud Peeter Hein Vedruka k\xFClast Kihelkonna kihelkonnast.",B=[{src:"kyla-havimine.webp"}],P=[["dis","dat"]],T=["rahvajutt","murdekeel","Kihelkonna","k\xFCla","soo"],A={__name:"miks-kyla-avis",setup(b,{expose:o}){const e={title:"Miks k\xFCla \xE4vis?",slug:"miks-kyla-avis",author:"rahvajutt",origin:"Jutustanud Peeter Hein Vedruka k\xFClast Kihelkonna kihelkonnast.",images:[{src:"kyla-havimine.webp"}],dictionary:[["dis","dat"]],tags:["rahvajutt","murdekeel","Kihelkonna","k\xFCla","soo"]};return o({frontmatter:e}),(w,K)=>{const u=t("story-author"),n=t("details-wrapper"),d=t("MarkdownWrapper");return k(),r(d,{frontmatter:e},{default:s(()=>[a("div",h,[a("h1",c,m(e.title),1),p,g,_,v,j,V,i(u,{author:e.author,origin:e.origin},null,8,["author","origin"]),i(n,{summary:"M\xF5tlemiseks ja arutlemiseks"},{default:s(()=>[y]),_:1}),i(n,{summary:"Allikad",class:"text-sm",icon:"IconSources"},{default:s(()=>[f]),_:1})])]),_:1})}}};export{J as author,A as default,P as dictionary,B as images,N as origin,S as slug,T as tags,M as title};
