import{h as i,o as k,b as m,k as l,a,t as r,i as t,m as s}from"./vendor.518a3394.js";const g={class:"markdown-body"},p={class:"story-h1"},j=a("p",null,[a("strong",null,"Jakob M\xE4ndmetsa"),s(" nimega puutusin esmakordselt kokku kuuendas klassis. Seal leidus kaks M\xE4ndmetsa novelli - \u201CKatkiraiutud kaljas\u201D ja \u201CKiir kaugusest\u201D. M\xF5lemad j\xE4id kustumatult meelde oma dramaatilise ps\xFChholoogilise situatsiooni ja sisemise valuga. Kaljas oli s\xFCdamaapoisile k\xFCll tundmatu s\xF5na, kuid t\xE4htsamad olid inimestevahelised suhted. Ja need kiskusid kaasa: v\xF5isin ette kujutada neid rannataate, kelle pilke ei kannatanud v\xE4lja Laine Andres ja l\xF5puks merre l\xE4ks.")],-1),v=a("p",null,"Keskkoolis mul M\xE4ndmetsaga tegemist ei tulnud - kooliprogrammi ta ei kuulunud. Ka \xFClikooli kirjanduseksamitel v\xF5is p\xE4rast s\xF5da ilma temata enam-v\xE4hem toime tulla. Kuid siiski sattusin p\xE4rast kolmandat kursust lugema Jakob M\xE4ndmetsa. Lugesin ja olin h\xE4mmastunud, miks sellist head kirjanikku pole t\xE4hele pandud, miks vaikivad temast kirjanduslood, miks ei anta teda uuesti v\xE4lja.",-1),h=a("p",null,"Sellal polnud mul ka kuigi palju kujutlust teadusliku uurimise tehnikast. \xDClikooli seminarides oli k\xFCll kursuset\xF6\xF6de kirjutamise puhul \xFCht-teist \xF5petamist, eriti tsiteerimist ja viidete aparatuuri, aga seda k\xF5ike j\xE4i v\xE4heseks. T\xF6\xF6 k\xE4igus tuli palju muudki \xF5ppida. (Tartu) Kirjandusmuuseum oli k\xFCll M\xE4ndmetsa k\xE4sikirjalise p\xE4randi sugulaste k\xE4est oma keldrisse toonud, selle korralikult kaante vahele paigutanud, numereerinud, mappideks jaganud ja katalogiseerinud, kuid edasi pidi uurija oma pea t\xF6\xF6le panema.",-1),c=a("p",null,"Ka oli h\xE4davajalik eluloolisi andmeid t\xE4iendada sugulaste k\xFCsitlemisega. Ei j\xE4\xE4nud muud \xFCle kui Tallinna s\xF5ita. Olin selleks ajaks juba viiendale kursusele j\xF5udnud ning minu M\xE4ndmetsaga tegelemist arvestades anti sellelt ainealalt diplomit\xF6\xF6 teema.",-1),_=a("p",null,"Kirjaniku sugulased leidsin k\xE4tte v\xE4iksest kahekorruselisest elamust Rahum\xE4e m\xE4ndide varjus. Vabaduse puiesteelt Veere t\xE4navale p\xF6\xF6rates ei teadnud ma sedagi, et j\xE4tsin selja taha maja, kus k\xFCmmekond aastat oli luuletanud Marie Under ja n\xFC\xFCd kirjutas oma marginaale Friedebert Tuglas. Tollesse majja leidsin tee alles aastaid hiljem.",-1),K=a("p",null,"Kirjaniku poeg Heino M\xE4ndmets otsis sahtlist v\xE4lja vanu ajalehelel\xF5ikeid ja kirjaridu, \xFCht-teist sain teada vestluse varal. Aga veel rohkem j\xE4i teadmatuse katte alla: ei osanud algaja kirjandusloolane k\xFCsitleda, M\xE4ndmetsad paistsid aga silma loomup\xE4rase kinnisusega. On ju Jakob M\xE4ndmets isegi oma eluloovisandis lausa t\xF5rjuvalt endast jutustamise lakooniliste lausetega l\xF5petanud: \u201CP\xE4rastisest elust ei ole mul midagi \xFCtelda. Kooli\xF5petaja ametis sain kodumaad nii m\xF5ndagipidi tundma, r\xE4ndasin ennemalt nii m\xF5nigi kord kodumaal ringi. Sest ajast saadik on mind alatine t\xF6\xF6 taga ajanud - t\xF6\xF6 lihtsalt \xFClespidamise m\xF5ttes. Muidugi on olnud ka eredaid ja valgeid silmapilke, aga nad on olnud silmapilgud, millega ei ole teistel asja ja mida keegi teine ei m\xF5istagi.\u201D",-1),b=a("p",null,"Polnud ma n\xE4inud isegi kirjaniku s\xFCnnipaika, Saaremaa oli minu jaoks terra incognita.",-1),M=a("p",null,"Reis Saaremaale on aegan\xF5udev: esimesel p\xE4eval j\xF5udsin ainult Kingisseppa. Kasutasin peatust mulle tundmatu linna uudistamiseks\u2026J\xE4rgmise p\xE4eva varahommikul s\xF5itsin Kingissepast bussiga Karjuj\xE4rvele. Oli juba 26. oktoober ja mandril puud raagus. Saaremaa s\xFCgis astub aeglasema sammuga ja \xFCle Karuj\xE4rve n\xE4gin veepeeglis end vahtimas veel m\xF5ningaid kollaseid kaski. Kuid maanteepoolses servas veekogu \xE4\xE4ristav p\xF5\xF5stastik meenutas luuahagu ning alles k\xF5dunemata k\xE4nnud r\xE4\xE4kisid inimese h\xE4vitust\xF6\xF6st. Ei p\xE4\xE4senud enam ka j\xE4rve kaldale - \xFCmbritsev soor\xF5ngas lirtsus jalge all hoiatavalt. Minu pilgule avanenud s\xFCgisene nukker, laastatud rannamaaga Karuj\xE4rv polnud hoopiski see, millest oli kirjutanud M\xE4ndmets. P\xFC\xFCdsin j\xE4rve kujutleda kirjaniku kirjelduste j\xE4rgi sonnustel suve\xF6\xF6del, kui v\xE4hilised hiilivad kahvu kaema m\xF6\xF6da \xF5tsku ning veepinnalt kerkivad udupahmakad moodnuvad viirastuslikeks kujudeks - mardusteks, n\xE4kkideks, vetevaimudeks\u2026Minu ees valendas aga hallis ja jahedas s\xFCgishommikus \xFCksluine veev\xE4li. K\xFCllap oli osa h\xE4da vaatlejaski, kes polnud siinsetel vetel pedajakoorest laevu ujutanud ega \xF5ngelatti leotanud.",-1),S=a("p",null,"Paik\xFCla pole Karjuj\xE4rvest kaugel. Tee l\xE4heb l\xE4bi metsa, laskudes korraks alla v\xE4ikesest n\xF5lvakust. Kahel pool saadavad r\xE4ndajat rahulikud pedajad, millede selja tagant uudishimutseb kaski ja kuuski. Siin-seal on tammgi oma oksad v\xF5imukalt laiali sirutanud. V\xE4heldasel metsalagendikul konutas m\xF5lemal pool teed halle hooneid. Talud p\xFC\xFCdsid end \xFCksteisest eraldada nurmesiilude ja aiapuudega, ei t\xFCkkinud mesilasperena s\xFClemisse nagu Koguvas v\xF5i Igak\xFClas. Ent majade ja p\xF5llumaade taga l\xF5id metsad j\xE4lle oma h\xF5lmad kokku. Kuidagi kinnise, endassesuletud ilmega n\xE4is see inimeste elupaik. Ent niisugune pidigi olema Paik\xFCla. Oli ju M\xE4ndmets kirjutanud oma eluloovisandis: \u201CV\xE4he tundus seal endisel ajal valju tuult ja tormi. Kui mujalt teated tulid valjudest kahjutegevatest marudest, siis n\xE4gime meie ainult k\xF5rgete puude k\xF5ikuvaid latvu.\u201D Suvel, kui uibud ja pooppuud mattuvad haljusesse, peab neil taludel \xF5nnestuma eralduda \xFCksteisest veelgi rohkem. Ja merest polnud siin j\xE4lgegi, k\xFCla tundus t\xE4iesti mandrilisena. Hakkasin m\xF5istma, miks M\xE4ndmets alustas rannajuttude kirjutamist alles oma loomingulise tee keskj\xE4rgus ja sellalgi vaatas merele romantiku pilguga - meri ei kuulnud tema lapsep\xF5lvekogemusse, vaid j\xE4i kuhugi unistuslikku kaugusesse, metsade taha.",-1),f=a("p",null,"Ainult Jakob M\xE4ndmetsa raamatutes elas see maailm edasi endise v\xE4rskusega. Muidugi, nagu iga kirjandus ikka, on noodki teosed osalt fantaasiatooted, kus tegelikkuse peegeldusse p\xF5imuvad autori kujutlused, unistused ja oletused.",-1),J=a("p",null,"3. novembril 1961 t\xF5i \u201CSirp ja Vasar\u201D teate, et l\xFChimonograafia \u201CJakob M\xE4ndmets\u201D on ilmunud 4000 eksemplaris ja maksab 30 kopikat. Ja t\xF5epoolest v\xF5is nii autor kui ka lugejad varsti toda kollasekaanelist raamatukest kaupluste riiulitel n\xE4ha. Sinna ta \xF5nneks ei j\xE4\xE4nud; suuremat t\xE4helepanu l\xFChimonograafia k\xFCll ei \xE4ratanud, kuid paari-kolme aasta p\xE4rast polnud v\xF5imalik teda enam osta.",-1),O=a("p",null,"Olin niisiis tolle vaikseloomulise Saaremaa mehega tegelenud k\xFCmme aastat. \xDChe \xF5hukese raamatu jaoks on seda muidugi liiga palju, kuid toda esikteost tuleb pidada m\xF5neski suhtes katset\xF6\xF6ks, mille varal \xF5ppisin kirjutama.",-1),V=a("ul",null,[a("li",null,"Oled sa Karuj\xE4rve \xE4\xE4res k\xE4inud? Mida tegemas?"),a("li",null,"Kirjelda m\xF5nd Saaremaa kohta, nt \xFCht Saaremaa k\xFCla (nagu \xFClal tekstis Paik\xFCla)."),a("li",null,"On sul m\xF5ni teema v\xF5i isik, mille/kelle uurimisele p\xFChendaksid 10 aastat oma elust? Mis v\xF5i kes? Maailmas, Eestis, Saaremaal?")],-1),y=a("ul",null,[a("li",null,[s("Allikas: "),a("strong",null,"Oskar Kruus"),s(", "),a("strong",null,"Endel Priideli"),s(" kogumikus \u201CTeose s\xFCnd\u201D, 1976.")]),a("li",null,[s("Foto: "),a("strong",null,"Merit Karise")])],-1),x="J\xE4rv on punutud muinasjuttude ja luule krooniga, mis tegid ta veidi \xF5udseks, aga selle eest otse veel ihaldavamaks",A="oskar-kruus-mandmets",B="Oskar Kruus",D=null,N=[{src:"kruus-mandmets.webp"}],C=[["ei","ole"]],F=["Debora Vaarandi"],H={setup(T,{expose:n}){const e={title:"J\xE4rv on punutud muinasjuttude ja luule krooniga, mis tegid ta veidi \xF5udseks, aga selle eest otse veel ihaldavamaks",slug:"oskar-kruus-mandmets",author:"Oskar Kruus",origin:null,images:[{src:"kruus-mandmets.webp"}],dictionary:[["ei","ole"]],tags:["Debora Vaarandi"]};return n({frontmatter:e}),(w,P)=>{const d=i("story-author"),u=i("details-wrapper"),o=i("MarkdownWrapper");return k(),m(o,{frontmatter:e},{default:l(()=>[a("div",g,[a("h1",p,r(e.title),1),j,v,h,c,_,K,b,M,S,f,J,O,t(d,{author:e.author,origin:e.origin},null,8,["author","origin"]),t(u,{summary:"M\xF5tlemiseks ja arutlemiseks"},{default:l(()=>[V]),_:1}),t(u,{summary:"Allikas",class:"text-sm",icon:"IconSources"},{default:l(()=>[y]),_:1})])]),_:1})}}};export{B as author,H as default,C as dictionary,N as images,D as origin,A as slug,F as tags,x as title};
