import{h as o,o as n,b as r,k as t,a,t as d,i as l,m as e}from"./vendor.518a3394.js";const m={class:"markdown-body"},h={class:"story-h1"},p=a("p",null,[a("em",null,[e("\u201CKui ma noor oli, siis oli n\u2019\xE4\xE4risokk sii arilik asi, ilma sokuta pole n\u2019\xE4\xE4ribed olnd. Sokud n\u2019\xE4\xE4ribe toojad olid ee\u2019ka. Ma ole ise sokku teind ja k\xFCla kauda sokuks k\xE4ind sii k\xFCll."),a("br"),e(" Vahest oli selg veel m\xFCtu a\xE8ga tagant j\xE4rge aige, ole k\xF5veras, look jalge vahel, obuse riie \xF6le, puksi koa ja kasta \xE4nda, karguta ja tee oma soku tegusi, ee\u2019ka. Aga see pole kerge, vahest v\xF5ttis \xE4\xE4le \xE4\xE4 see kange m\xF6kitamine ja m\u2019\xE4\xE4mine, pakase k\xFClmaga k\xFClavahel lume sihes, sojast toast tulles, nahk pala, ee\u2019ka. Aga see oli siis moed. \xD6\xF6, p\xE4\xE4 veel tagant o\u2019tsa koa, j\xF5\xF5ra 4 sokuga k\xFCla kauda. Vahest mindi j\xE4rjega teise kolmandase k\xFClase. P\xE4\xE4va kahe kolme p\xE4rast sai tagasi, see oli ee\u2019ka."),a("br"),e(" See oli ee\u2019ka nii vana asi, et \xE4i meite isad ja isaisad pole koa seda r\xE4\xE4\u2019kind, et mis ajast ja mis p\xE4\xE4vast see asi akkand on, ja kust see tulnd on, \xE4i tea, ee\u2019ka.\u201D")])],-1),c=a("p",null,"Georg M\xE4gi Paju-Kurdla k\xFClas, Laimjala",-1),j=a("p",null,[e("Saaremaal on paiguti t\xE4nini eluj\xF5us kristluse-eelne sokujooksmise komme: sokuks maskeerunud mees k\xE4ib koos saatjatega uusaasta\xF6\xF6l perest perre, m\xF6kitab, puksib, tantsib, soovib uueks aastaks \xF5nne. Pererahvas pakub talle meelehead (\xF5lut, viina, \xF5unu, pirukaid, komme jms). Lahkudes kirjutab sokk kriidiga maja uksele uue aasta numbri. Kui eri peredest tulnud sokud teel kohtuvad, puksitakse omavahel. Vahel v\xF5ib juhtuda, et puksimine l\xE4heb \xFCle suuremaks raginaks ja j\xF5ukatsumiseks, mille k\xE4igus sokud purunevad. T\xE4pseid andmeid selle kohta, millistes Saaremaa nurkades uusaasta\xF6\xF6l sokku joostakse, ei ole teada. K\xFCll aga on kindel, et n\xE4\xE4risokku v\xF5ib kohata Pihtla vallas Haeskas, Leisi vallas Metsk\xFClas ja Laimjala vallas Randveres: "),a("a",{href:"https://www.youtube.com/watch?v=gvH0NthJ2io&t=1s"},"https://www.youtube.com/watch?v=gvH0NthJ2io&t=1s")],-1),v=a("ul",null,[a("li",null,"Oled sa sokku n\xE4inud, teinud v\xF5i neist kuulnud? K\xFCsi ka oma (vana)vanematelt!"),a("li",null,"Mis sa arvad, kas sellist kommet tasuks v\xF5i \xFCldse \xF5nnestub elus hoida? Miks? Kui jah, siis kuidas?"),a("li",null,[e("Saarema sokud on Eesti vaimse kultuurip\xE4randi osa: "),a("a",{href:"https://rahvakultuur.ee/2020/03/24/sokujooksmine-saaremaal/"},"https://rahvakultuur.ee/2020/03/24/sokujooksmine-saaremaal/")]),a("li",null,[e("Vaata, millised sanditajad on sinu kodukohas k\xE4inud: "),a("a",{href:"https://www.folklore.ee/Sanditajad/"},"https://www.folklore.ee/Sanditajad/")])],-1),g=a("p",null,[e("Sokkude illustratsioon: "),a("strong",null,"Oliver Maaker")],-1),w=a("ul",null,[a("li",null,[e("Murdetekst n\xE4\xE4risoku tegemisest: "),a("a",{href:"https://www.emakeeleselts.ee/valjaanne/tuulik-kadakas-ja-leib/"},"https://www.emakeeleselts.ee/valjaanne/tuulik-kadakas-ja-leib/")])],-1),M="Saaremaa sokud",y="saaremaa-sokud",V="rahvajutt",K="unknown",x=[{src:"saaremaa-sokud.webp"}],N=[["ei","ole"]],L=["sokud","santimine"],P={setup(_,{expose:u}){const s={title:"Saaremaa sokud",slug:"saaremaa-sokud",author:"rahvajutt",origin:"unknown",images:[{src:"saaremaa-sokud.webp"}],dictionary:[["ei","ole"]],tags:["sokud","santimine"]};return u({frontmatter:s}),(b,f)=>{const i=o("details-wrapper"),k=o("MarkdownWrapper");return n(),r(k,{frontmatter:s},{default:t(()=>[a("div",m,[a("h1",h,d(s.title),1),p,c,j,l(i,{summary:"M\xF5tlemiseks ja arutlemiseks"},{default:t(()=>[v]),_:1}),l(i,{summary:"Allikad",class:"text-sm",icon:"IconSources"},{default:t(()=>[g,w]),_:1})])]),_:1})}}};export{V as author,P as default,N as dictionary,x as images,K as origin,y as slug,L as tags,M as title};
