import{b as e,o as d,e as k,q as i,i as a,t as m,d as u,g as t}from"./vendor.e1ac141b.js";const p={class:"markdown-body"},h={class:"story-h1"},c=a("p",null,"See lugu leidis aset 50ndate alguses, peale s\xF5da.",-1),_=a("p",null,"Kord oli \xFCks t\xFCdruk Irase k\xFClas \xFCksinda koju j\xE4\xE4nud. V\xF5\xF5rasema oli kodust tol \xF5htul \xE4ra l\xE4inud. Oli s\xFCgis ja hiline aeg. T\xFCdruk luges k\xFC\xFCnlavalgel raamatut.",-1),g=a("p",null,"\xC4kki kuulis ta pudelite kolinat. See tuli ruumist, kus olid surnud majaomaniku asjad.",-1),v=a("p",null,"Nimelt paar p\xE4eva enne seda oli perenaine lahkunud siit ilmast. Ta laip oli veel seal majas. Enne surma oli naine soovinud, et teatud inimesed ta isiklikke asju endale ei v\xF5taks. T\xFCdruk arvas, et kolinat teeb surnud perenaine, kes oli tulnud kontrollima, et ta asjad k\xF5ik olemas oleks. Kolin kestis t\xFCkk aega ja l\xF5puks saabus vaikus. T\xFCdruk j\xE4i magama.",-1),j=a("p",null,"Hommikul lauta minnes pidi ta l\xE4bi minema ruumist, kus surnu asus. Imestus oli suur, sest laiba pealt oli lina \xE4ra kukkunud. T\xFCdruk oli t\xE4iesti kindel, et eelmisel \xF5htul oli kolistanud just tema, kes oli mures oma asjade p\xE4rast.",-1),w=a("p",null,"Peale matust oli aeg-ajalt olnud kolinat kuulda.",-1),f=a("p",null,[a("em",null,"See t\xFCdruk oli minu vanaema (Aino Kask, 78-aastane)")],-1),b=a("ul",null,[a("li",null,"Kas sa oled oma (vana)vanematelt v\xF5i teiselt sugulastelt kuulnud lugusid, mis kananaha ihule toovad? Kui jah, siis milliseid?"),a("li",null,"Kas tead, kuhu on sinu sugulased maetud (kui nad on maetud Saaremaale)? K\xE4id sa surnuaial? Kui tihti ja millal?"),a("li",null,[t("Millised olid saarlaste uskumused surnute osas? Siit saad lugeda: "),a("a",{href:"https://www.facebook.com/maavald/posts/10157536727031497"},"https://www.facebook.com/maavald/posts/10157536727031497")]),a("li",null,[t("Miks hoiti vanasti surnut kodus? Siit saad lugeda: "),a("a",{href:"https://www.maavald.ee/maausk/maausust/eluring/157-kui-inimene-sureb"},"https://www.maavald.ee/maausk/maausust/eluring/157-kui-inimene-sureb")]),a("li",null,[t("Kui sind hakkasid matmiskombed huvitama, siis siit saab lugeda k\xFCttide ja korilaste matmiskommete kohta: "),a("a",{href:"https://www.ajakiri.ut.ee/artikkel/1959"},"https://www.ajakiri.ut.ee/artikkel/1959")])],-1),K=a("ul",null,[a("li",null,[t("Tondijutt: "),a("a",{href:"http://saaremaa.folklore.ee/wordpress/?p=1124"},"http://saaremaa.folklore.ee/wordpress/?p=1124")]),a("li",null,[t("Foto: "),a("strong",null,"Merit Karise")])],-1),x="Murelik surnu",A="murelik-surnu",N="rahvajutt",V="Saaremaa tondijuttude kogumisv\xF5istluse k\xE4igus kirja pannud Kerli Aksalu.",B=[{src:"murelik-surnu.webp"}],I=[["kiiva","viltu"],["lititsema","lesta liikumise kohta"]],C=["rahvajutt","surm","Kaarma"],W={setup(S,{expose:o}){const s={title:"Murelik surnu",slug:"murelik-surnu",author:"rahvajutt",origin:"Saaremaa tondijuttude kogumisv\xF5istluse k\xE4igus kirja pannud Kerli Aksalu.",images:[{src:"murelik-surnu.webp"}],dictionary:[["kiiva","viltu"],["lititsema","lesta liikumise kohta"]],tags:["rahvajutt","surm","Kaarma"]};return o({frontmatter:s}),(y,M)=>{const n=e("story-author"),l=e("details-wrapper"),r=e("MarkdownWrapper");return d(),k(r,{frontmatter:s},{default:i(()=>[a("div",p,[a("h1",h,m(s.title),1),c,_,g,v,j,w,f,u(n,{author:s.author,origin:s.origin},null,8,["author","origin"]),u(l,{summary:"M\xF5tlemiseks ja arutlemiseks"},{default:i(()=>[b]),_:1}),u(l,{summary:"Allikad",class:"text-sm",icon:"IconSources"},{default:i(()=>[K]),_:1})])]),_:1})}}};export{N as author,W as default,I as dictionary,B as images,V as origin,A as slug,C as tags,x as title};
